#!/usr/bin/perl  ## reformat the data files #  written by Chris Basten, 10/28/97#my %alloc;my $line;my %dealloc;my @fields;my $address;my $ad;my $u;while ( <> ) {  chop;  $line = $_;  $line =~ s/deallocated//g;  $line =~ s/allocated//g;  $line =~ s/In//g;  $ad = 1;  $ad = 0 if ( /deallocated/ ) ;  @fields = split ;  $address = $fields[$#fields];  if ( $ad == 1 ) {    while ( $alloc{$address}  eq "" ) {      $address = $address . "+";    }    $alloc{$address} = $line  ;  }  if ( $ad == 0 ) {    while ( $dealloc{$address}  eq "" ) {      $address = $address . "+";    }    $dealloc{$address} = $line   ;    }}foreach $u (sort  keys %alloc ) {	printf("%8s %40s %40s\n", $u, $alloc{$u}, $dealloc{$u});}printf("\n\n");foreach $u (sort  keys %dealloc ) {	printf("%8s %40s %40s\n", $u, $alloc{$u}, $dealloc{$u});}